const BACKEND_URL = process.env.REACT_APP_BACKEND_URL;
const API = `${BACKEND_URL}/api`;

const Portfolio = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [activeSection, setActiveSection] = useState("hero");
  const [visitorCount, setVisitorCount] = useState({
    total_visitors: 0,
    unique_visitors: 0,
    today_visitors: 0
  });

  useEffect(() => {
    // Track visitor on page load
    const trackVisitor = async () => {
      try {
        const response = await fetch(`${API}/visitors/track`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            page: '/',
            user_agent: navigator.userAgent
          })
        });

        if (response.ok) {
          const result = await response.json();
          console.log('Visitor tracked successfully');
        }
      } catch (error) {
        console.error('Error tracking visitor:', error);
      }
    };

    // Get visitor count
    const getVisitorCount = async () => {
      try {
        const response = await fetch(`${API}/visitors/count`);
        if (response.ok) {
          const count = await response.json();
          setVisitorCount(count);
        }
      } catch (error) {
        console.error('Error fetching visitor count:', error);
      }
    };

    trackVisitor();
    getVisitorCount();

    // Update visitor count every 30 seconds
    const interval = setInterval(getVisitorCount, 30000);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    const handleScroll = () => {
      const sections = ["hero", "about", "skills", "experience", "projects", "contact"];
      const scrollPosition = window.scrollY + 100;

      for (let section of sections) {
        const element = document.getElementById(section);
        if (element) {
          const offsetTop = element.offsetTop;
          const offsetHeight = element.offsetHeight;

          if (scrollPosition >= offsetTop && scrollPosition < offsetTop + offsetHeight) {
            setActiveSection(section);
            break;
          }
        }
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <footer className="footer">
      <div className="container">
        <div className="footer-content">
          <div className="footer-stats">
            <div className="visitor-stats">
              <Users size={16} />
              <span>
                {visitorCount.total_visitors} Total Visitors | 
                {visitorCount.unique_visitors} Unique | 
                {visitorCount.today_visitors} Today
              </span>
            </div>
          </div>
          <p>&copy; 2025 Himanshu Srivastava. All rights reserved.</p>
          <p>Built with passion for clean code and elegant solutions.</p>
        </div>
      </div>
    </footer>
  );
};

export default Portfolio;
